import Link from "next/link";
import React from "react";
import styles from "./styles";
import { useRouter } from "next/router";
import { Container } from "@material-ui/core";

export default function Header({ categories }) {
  const router = useRouter();
  return (
    <Container
      maxWidth="xl"
      className="container"
      style={{ paddingLeft: 0, paddingRight: 0, marginBottom: 30 }}
    >
      <h3 className="ui dividing header container-search">
        <div className="header-logo">
          <a>
            <img
              alt="logo"
              src="/static/assets/images/logo.png"
              className="logo"
            />
          </a>
          <div className="ui small left icon transparent input header-search">
            <input type="text" placeholder="Search..." />
            <i aria-hidden="true" className="search icon"></i>
          </div>
        </div>
      </h3>
      <div className="ui secondary menu nav-menu">
        <Link href="/">
          <a
            className={`${
              router.asPath.substring(1) === "" ? "active" : ""
            } item`}
          >
            Trang chá»§
          </a>
        </Link>
        {categories &&
          categories.map((category) => (
            <Link key={category.id} href={`/${category.slug}`}>
              <a
                className={`${
                  router.asPath.substring(1) === category.slug ? "active" : ""
                } vertically fitted item`}
              >
                {category.title}
              </a>
            </Link>
          ))}
      </div>
      <style jsx>{styles}</style>
    </Container>
  );
}
