import Link from "next/link";
import React from "react";
import styles from "./styles";
import { useRouter } from "next/router";
import { Container } from "@material-ui/core";
import {ReactComponent as ReactLogo} from '../../static/assets/images/logo_web.svg';


export default function Header({ categories }) {
  const router = useRouter();
  return (
    <React.Fragment>
      <Container maxWidth="xl" className="container">
        <div>
          <h3 className="ui dividing header container-search">
            <div className="header-logo">
              <a>
                <ReactLogo />
              </a>
              <div className="ui small left icon transparent input header-search">
                <input type="text" placeholder="Search..." />
                <i aria-hidden="true" className="search icon"></i>
              </div>
            </div>
          </h3>
          <div className="ui secondary menu nav-menu">
            <Link href="/">
              <a
                className={`${
                  router.asPath.substring(1) === "" ? "active" : ""
                } item`}
              >
                Trang chá»§
              </a>
            </Link>
            {categories &&
              categories.map((category) => (
                <Link key={category.id} href={`/${category.slug}`}>
                  <a
                    className={`${
                      router.asPath.substring(1) === category.slug
                        ? "active"
                        : ""
                    } item`}
                  >
                    {category.title}
                  </a>
                </Link>
              ))}
          </div>
        </div>
        <style jsx>{styles}</style>
      </Container>
    </React.Fragment>
  );
}
