import Link from "next/link";
import React, { useState } from "react";
import styles from "./styles";
import { useRouter } from "next/router";

export default function Header({ categories }) {
  const router = useRouter();
  const [txtSearch, setTxtSearch] = useState("");

  const onSearch = () => {
    if (txtSearch !== "") {
      console.log(txtSearch);
      router.push(`/search?query=${txtSearch}`, {}, { query: txtSearch });
    }
  };

  const onChangeTxtSearch = (evt) => {
    setTxtSearch(evt.target.value);
  };

  return (
    <div className="container">
      <h3 className="ui dividing header container-search">
        <div className="header-logo">
          <a href="/">
            <img
              alt="logo"
              src="/static/assets/images/logo.png"
              className="logo"
            />
          </a>
          <div className="ui icon input transparent header-search">
            <input
              type="text"
              placeholder="Search..."
              onChange={onChangeTxtSearch}
            />
            <i
              aria-hidden="true"
              className="search circular link icon icon-search"
              onClick={onSearch}
            ></i>
          </div>
        </div>
      </h3>
      <div className="ui secondary menu nav-menu">
        <Link href="/">
          <a
            className={`${
              router.asPath.substring(1) === "" ? "active" : ""
            } item`}
          >
            Trang chá»§
          </a>
        </Link>
        {categories &&
          categories.map((category) => (
            <Link key={category.id} href={`/${category.slug}`}>
              <a
                className={`${
                  router.asPath.substring(1) === category.slug ? "active" : ""
                } item`}
              >
                {category.title}
              </a>
            </Link>
          ))}
      </div>
      <style jsx>{styles}</style>
    </div>
  );
}
